  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  const Header = (props) => {
    console.log(props)
    return <h1>Name : {props.course}</h1>
  }

  const Content = (props) => {
    return (
      <div>
        <p>

          <p>
        Part1: part1.exercises
      </p>
      <p>
       Part2: part2.exercises
      </p>
      <p>
       Part3: part3.exercises
      </p>
        </p>
      </div>
    )
  }
  const Total = (props) => {
    return (
      <div>
        <p>
          Total is {props.all}
          <p>Number of exercises :{exercises1 + exercises2 + exercises3}</p>
        </p>

      </div>
    )
  }



  return (

    <div>
      <Header course={course} />
      <Content  name ={name}/>
      <Total all={all} />

    </div>

  )
}




export default App
