import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  const Header = (props) => {
    console.log(props)
    return <h1>Name : {props.course}</h1>
  }

  const Content = (props) => {
    return (
      <div>
        <p>

        Part1: {console.log(part1.exercises)}
      </p>
      <p>
       Part2: {console.log(part2.exercises)}
      </p>
      <p>
       Part3: {console.log(part3.exercises)}
      </p>

      </div>
    )
  }
  const Total = (props) => {
    return (
      <div>
        <p>
          Total is {props.all}
          <p>Number of exercises :{console.log(part3.exercises)}</p>
        </p>

      </div>
    )
  }



  return (

    <div>
      <Header course={course} />
      <Content  />
      <Total  />

    </div>

  )
}




export default App
